import React, { Component } from "react";
import { FaBath, FaBeer, FaBrain, FaTrash, FaTrashRestoreAlt } from 'react-icons/fa';
class TodoItem extends Component {
 
constructor(props) {
	super(props);
	this.ListTasks=this.ListTasks.bind(this);

}

ListTasks(task){


	return <div className="Task"><li onClick={() => console.log('kliknieto mnie')} key={task.key}> 
	<div className="ContainerBox"><input type="checkbox" className="check"></input></div>
{task.text}

<button><i class="fa fa-trash-o"></i><FaTrash /></button>
	
	</li></div>

}



	render(){
		var todoEntity=this.props.entrie;
		console.log('to jest todoEntity '+ todoEntity);
		// todoEntity stala sie array bo dalismy taki argument gdziue wywolujemy to 
		var getItems=todoEntity.map(this.ListTasks); //ttodoEntity its arrived array from TodoList.js
		return (

		<ul className="theList">
		{ getItems}
	</ul>

		);
	}

}

export default TodoItem;
