import React, { Component } from "react";
import { FaBath, FaBeer, FaBrain, FaTrash, FaTrashRestoreAlt } from 'react-icons/fa';
class TodoItem extends Component {
 
constructor(props) {
	super(props);
	this.ListTasks=this.ListTasks.bind(this);
	 this.DeleteItem=this.DeleteItem.bind(this);

this.state={candidateSelected: false};

}


verifiedChange (id)  {
	// e.preventDefault(); It's not needed


	this.setState({
	  verified: !this.state.candidateSelected // It will make the default state value(false) at Part 1 to true 
	});
		const requestOptions = {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({Id:id,task:task, isCompleted: this.state.candidateSelected })
		
    };    fetch(`https://localhost:3000/api/todoitems/${id}`, requestOptions)
	.then(console.log("operation comlete"));
    

  }; 



DeleteItem(idToDelete){
	
	const configToDelete={
		method: "DELETE",
		headers: {'Content-Type': 'application/json'},
	
	}
	fetch(`https://localhost:3000/api/todoitems/${idToDelete}`,configToDelete).then(console.log("delete item: " + idToDelete));
}





ListTasks(task){
	// onClick={() =>this.onSelectCandidatesClick(task.id,task.task) }

	return <div className="Task"><li key={task.id}onClick={() => console.log(task.id)}> 
	<div className="ContainerBox"><input type="checkbox"  value={this.state.candidateSelected}   onChange={this.verifiedChange(task.id)}   className="check"></input></div>
<div className='taskName'>{task.task}

<button  onClick={() => this.DeleteItem(task.id)} className='trashBtn'><i className="fa fa-trash-o"></i><FaTrash /></button>
</div>

	
	</li></div>

}



	render(){
		var memberTask=this.props.memberTask;
		// let i=memberTask.map(w=>this.ListTasks(w));
		// let context=i?"DWD";
	
		return (

		<ul className="theList">
	{/* {homes.map(home => <div>{home.name}</div>)} */}
		{memberTask.map(w=>this.ListTasks(w))}
	
	
	</ul>

		);
	}

}

export default TodoItem;
